// a basic structure for todo-list app step by step

import React, { useState } from 'react';
import './App.css';
function App() {
  // Step 3: Add State for Tasks using the `useState` Hook:
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
 // step 4 implement Add Task Function
 function handleInputChange(e) {
  setNewTask(e.target.value);
  }
  function addTask() {
  if (newTask.trim() !== "") {
  const newTaskItem = { id: Date.now(), text: newTask, completed: false };
  setTasks([...tasks, newTaskItem]);
  setNewTask("");
  }
  }
  //Step 5: Mark Task as Complete
  function toggleCompletion(id) {
    const updatedTasks = tasks.map(task => {
    if (task.id === id) {
    return { ...task, completed: !task.completed };
    }
    return task;
    });
    setTasks(updatedTasks);
    }
    //Step 6: Filter Tasks
    const [filter, setFilter] = useState("all"); // all, active, completed
    // get a filter started
    const filteredTasks = tasks.filter(task => {
      if (filter === "active") return !task.completed;
      if (filter === "completed") return task.completed;
      return true;
      });

    // Step 7: Complete UI

return (
  <div className="App">
  <h1>To-Do List</h1>

  <input
    type="text"
    value={newTask}
    onChange={handleInputChange}
    placeholder="Add a new task..."
  />
  <button onClick={addTask}>Add</button>

  <ul>
    {filteredTasks.map(task => (
      <li key={task.id}>
        <input
          type="checkbox"
          checked={task.completed}
          onChange={() => toggleCompletion(task.id)}
        />
        {task.text}
      </li>
    ))}
  </ul>

  <div>
    Filter:
    <button onClick={() => setFilter("all")}>All</button>
    <button onClick={() => setFilter("active")}>Active</button>
    <button onClick={() => setFilter("completed")}>Completed</button>
  </div>

{/* Further code will go here */}
</div>
);
}
export default App;







